<script>
    $(document).ready(function(){
        $(".nav-tabs a").click(function(){
            $(this).tab('show');
        });
    });
    var tabLinks = document.querySelectorAll('.nav-tabs .nav-link');
    var tabPanels = document.querySelectorAll('.tab-pane');

    for (var i = 0; i < tabLinks.length; i++) {
        tabLinks[i].addEventListener('click', function (e) {
            e.preventDefault();
            var target = e.target;

            for (var j = 0; j < tabLinks.length; j++) {
                tabLinks[j].classList.remove('active');
            }

            target.classList.add('active');

            for (var k = 0; k < tabPanels.length; k++) {
                tabPanels[k].classList.remove('active');
            }

            var tabId = target.getAttribute('href');
            document.querySelector(tabId).classList.add('active');
        });
    }

</script>
